const Voter = require("../models/Voter");

function createVoter(application) {

  const voter = new Voter();

  voter.name = application.name;
  voter.gender = application.gender;
  voter.dob = application.dob;
  voter.phone = application.phone;
  voter.email = application.email;
  voter.address = application.address;
  voter.pincode = application.pincode;

  voter.save();

  console.log("User Applied Successfully for VoterID");

}

